import React, { useState } from 'react'
import noteContext from './noteContext'

const NoteState = (props) => {
   const noteInitsialized = [
    {
        "user": "66aa7627257bc137f4a4e45f",
        "title": "Hi ...1",
        "description": "Hello 1 ",
        "tag": "ABCD",
        "_id": "66af6bb542a370109f2cc40b",
        "date": "2024-08-04T11:53:25.531Z",
        "__v": 0
      },
      {
        "user": "66aa76272572bc137f4a4e45f",
        "title": "Hi ...2",
        "description": "Hello 2",
        "tag": "ABCD",
        "_id": "66af6bb542a370109f2cc40b",
        "date": "2024-08-04T11:53:25.531Z",
        "__v": 0
      }
   ]
   const [notes, setnotes] = useState(noteInitsialized)

   //Add a new Note....
   const addNotes = (title , description , tag)=>{
      const note = {
        "user": "66aa762725721bc137f4a4e45f",
        "title": title,
        "description": description,
        "tag": tag,
        "_id": "66af6bb542a370109f2cc40b",
        "date": "2024-08-04T11:53:25.531Z",
        "__v": 0
      }
      setnotes(notes.concat(note))
   }

   //Delete a Note...
   const deleteNote = (id)=>{
    const newNote = notes.filter((note)=>{note._id !== id}) 
    setnotes(newNote)
   }

   //Edit a Note...
   const editNote = (title , description , tag)=>{
    //A
    //This is Client side
    for(let index = 0 ; index<notes.length ; index++){
      const element = notes[index]
      if(element._id === id){
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
   }
    return (
        <noteContext.Provider value={{ notes , addNotes , deleteNote , editNote}}>
            {props.children}
        </noteContext.Provider>
    )
}

export default NoteState
