import React, { useContext , useState} from 'react'
import NoteContext from "../Constaxt/notes/noteContext"

const Addnote = () => {
    const context = useContext(NoteContext);
    const {Addnote}= context;

    const [notes, setnotes] = useState({title:"" , description : "" , tag:"Default"})
    const handlAdd = (e)=>{
        e.preventDefault()
        setnote(notes.title , notes.description , notes.tag)
    }

    const onChange = (e)=>{
        setnotes({...notes , [e.target.name] : e.target.value})
    }

    return (
        <>
            <div className="my-3 container">
                <h1>Add Notes</h1>
                <div className="  mb-3">
                    <label for="title" className="form-label" onChange={onChange}>Title</label>
                    <input type="text" className="form-control" id="title" name="title" placeholder="Enter Text ..." />
                </div>
                <div className=" mb-3">
                    <label for="description" className="form-label">Description</label>
                    <input type='text' className="form-control" id="description" name="description" rows="3" onChange={onChange}></input>
                </div>
                <button type="button" class="btn btn-primary" onClick={handlAdd}>Add Note</button>
            </div>
        </>
    )
}

export default Addnote
