import React, { useState } from 'react'

const Login = () => {
    const [credentials, setcredentials] = useState({ email: "", password: "" })

    const handlelogin = async (e) => {
        e.preventDefault()
        const response = await fetch(`http://localhost:5000/api/author/login`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ email: credentials.email, password: credentials.password }),
        });
        const json = await response.json()
        console.log(json);
    }
    const onnChange = (e) => {
        console.log(e);
        
        // setcredentials({ ...credentials, [e.target.name]: e.target.value }) 
    }
    return (
        <>
            <form className='my-3' onSubmit={handlelogin}>
                <div className="form-group">
                    <label htmlFor="email">Email address</label>
                    <input type="email" className="form-control" id="email" aria-describedby="emailHelp" placeholder="Enter email"  onChange={onnChange} />
                </div>
                <div className="form-group my-2">
                    <label htmlFor="password">Password</label>
                    <input type="password" className="form-control" id="password" placeholder="Password" value={credentials.password} onChange={onnChange} />
                </div>

                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </>
    )
}

export default Login
