import React, { useState } from 'react'
import noteContext from './noteContext'

const NoteState = (props) => {
    const noteinitsilal = []
    const [notes, setnotes] = useState(noteinitsilal)

    // Get All notes
    const getallnotes = async () => {
        const respons = await fetch("http://localhost:5000/api/notes/fatchallnotes", {
            method: "GET",
            headers: {
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZhYTc2MjcyNTdiYzEzN2Y0YTRlNDVmIn0sImlhdCI6MTcyMjYwMzYyNH0.X9rBNIV6BmBtHSOPAHXi9BlKCoKD-AO0uHOLW2NUtjM"
            }

        })
        const json = await respons.json();
        console.log(notes);
        
        setnotes(json)
    }


    //Add Notes 
    const addNotes = async ({title , description , tag}) => {
        const respons = await fetch("http://localhost:5000/api/notes/addnotes", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZhYTc2MjcyNTdiYzEzN2Y0YTRlNDVmIn0sImlhdCI6MTcyMjYwMzYyNH0.X9rBNIV6BmBtHSOPAHXi9BlKCoKD-AO0uHOLW2NUtjM"
            },
            body:JSON.stringify(notes)
            // body: {
            //     "title": title,
            //     "description": description,
            //     "tag": tag
            // }

        })
        const json = await respons.json();

        const noteone = {
            "_id": "66ae407645145a76caffbe210",
            "user": "66aa7627257bc137f4a4e45f",
            "title": title,
            "description": description,
            "tag": tag,
            "date": "2024-08-03T06:57:54.531Z",
            "__v": 0
          }
        setnotes(notes.concat(noteone))
    }

    // Delete Notes
    const deletenote = async (id) => {
        const respons = await fetch(`http://localhost:5000/api/notes/notedelete/${id}`, {
            method: "DELETE",
            headers: {
                "Content-Type": "application/json",
                "auth-token": "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZhYTc2MjcyNTdiYzEzN2Y0YTRlNDVmIn0sImlhdCI6MTcyMjYwMzYyNH0.X9rBNIV6BmBtHSOPAHXi9BlKCoKD-AO0uHOLW2NUtjM"
            }

        })
        const json = await respons.json();
        // setnote(json)
        const newnote = notes.filter((note)=>{return note._id !== id})
        setnote(newnote)
    }
    return (
        <noteContext.Provider value={{ notes, addNotes, deletenote, getallnotes }}>
            {props.children}
        </noteContext.Provider>
    )
}

export default NoteState
