import React, { useContext, useEffect, useRef } from 'react'
import NoteContext from "../Constaxt/notes/noteContext"
import Noteitem from "./Noteitem"
import Addnote from './Addnote'

const Note = () => {
  const context = useContext(NoteContext);
  const { notes, getallNotes } = context;
  useEffect(() => {
    getallNotes()
    // eslint-disable-next-line
  }, [])
  const [note, setnote] = useState({title:"" , description : "" , tag:"Default"})
    
  const handleAdd = (e)=>{
      e.preventDefault()
      // console.log(notes.description);
      addNotes(note.title , note.description , note.tag)
      
  }

  const onChange = (e)=>{
      setnote({...note , [e.target.name] : e.target.value})
  }
  const updateNote = (note) => {
    ref.current.click()
  }
  const ref = useRef(null)
  return (
    <>
      <Addnote />
      <button type="button" style={{ display: "none" }} ref={ref} className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Launch demo modal
      </button>

      <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h1 className="modal-title fs-5" id="exampleModalLabel">Modal title</h1>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
                <div className="mb-3">
                    <label htmlFor="etitle" className="form-label" >Title</label>
                    <input type="text" className="form-control" id="etitle" name="etitle" placeholder="Enter Text ..."onChange={onChange} />
                </div>
                <div className=" mb-3">
                    <label htmlFor="edescription" className="form-label">Description</label>
                    <input type='text' className="form-control" id="edescription" name="edescription"  onChange={onChange}></input>
                </div>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="button" className="btn btn-primary">Save changes</button>
            </div>
          </div>
        </div>
      </div>

      <div className="row ">
        <h1>Your Notes</h1>
        {
          notes.map((note) => {
            return <Noteitem note={note} updateNote={updateNote} key={note._id} />
          })
        }
      </div>
    </>
  )
}

export default Note
