import React, { useState } from 'react'
import noteContext from './noteContext'

const NoteState = (props) => {
    // Get All notes

    const getallnotes = async()=>{
        const respons = await fetch("http://localhost:5000/api/notes/fatchallnotes",{
            method:""
        })

    }
    const [note, setnote] = useState(notes)

    //Add Notes 
    const addNotes = (title, description, tag) => {
        const noteone = {
            "_id": "66acd8a0c49350987e86ba8b",
            "user": "66aa7627257bc137f4a4e45f",
            "title": title,
            "description": description,
            "tag": tag,
            "date": "2024-08-02T13:01:20.721Z",
            "__v": 0
        }

        setnote(note.concat(noteone))
    }

    // Delete Notes
    const deletenote = (id)=>{
        console.log(id);
        
        const newnote = note.filter((note)=>{return note._id !== id})
        setnote(newnote)
    }
    return (
        <noteContext.Provider value={{ note, addNotes , deletenote}}>
            {props.children}
        </noteContext.Provider>
    )
}

export default NoteState
