import React, { useState } from 'react'
import noteContext from './noteContext'

const NoteState = (props) => {
  const host = "http://localhost:5000"
   const noteInitsialized = []
   const [notes, setnotes] = useState(noteInitsialized)

 //GetAll Notes....
 const getallNotes = async()=>{
  //API Call
  const response = await fetch(`${host}/api/notes/fatchallnotes`, {
    method: "GET",
    headers: {
      "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZhYTc2MjcyNTdiYzEzN2Y0YTRlNDVmIn0sImlhdCI6MTcyMjQ0NzQ3NH0.uW3CuxfuuWg_E5y5lGAq1FTZHITe650JMyQrD0mk9rc",
    },
  });
  const json = await response.json()
  setnotes(json)
}


   //Add a new Note....
   const addNotes = async(title , description , tag)=>{
    //API Call
    const response = await fetch(`${host}/api/notes/addnotes`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZhYTc2MjcyNTdiYzEzN2Y0YTRlNDVmIn0sImlhdCI6MTcyMjQ0NzQ3NH0.uW3CuxfuuWg_E5y5lGAq1FTZHITe650JMyQrD0mk9rc",
      },
      body: JSON.stringify({title , description , tag}),
    });
    const json = await response.json()
    //This is Client side
      const note = {
        "user": "66aa76272572da1bc137f4a4e45f",
        "title": title,
        "description": description,
        "tag": tag,
        "_id": "66af6bb542a370109f2cc40b",
        "date": "2024-08-04T11:53:25.531Z",
        "__v": 0
      }
      setnotes(notes.concat(note))
   }




   //Delete a Note...
   const deleteNote = async(id)=>{
    //API Call
    const response = await fetch(`${host}/api/notes/notedelete/${id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZhYTc2MjcyNTdiYzEzN2Y0YTRlNDVmIn0sImlhdCI6MTcyMjQ0NzQ3NH0.uW3CuxfuuWg_E5y5lGAq1FTZHITe650JMyQrD0mk9rc",
      },
     
    });
    const json = await response.json();
    //This is Client side
    const newNote = notes.filter((note)=>{return note._id !== id}) 
    setnotes(newNote)
   }

   //Edit a Note...
   const editNote = async(id,title , description , tag)=>{
    //API Call
    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZhYTc2MjcyNTdiYzEzN2Y0YTRlNDVmIn0sImlhdCI6MTcyMjQ0NzQ3NH0.uW3CuxfuuWg_E5y5lGAq1FTZHITe650JMyQrD0mk9rc",
      },
      body: JSON.stringify({title , description , tag}),
    });
    // const json = await response.json()
    //This is Client side
    for(let index = 0 ; index<notes.length ; index++){
      const element = notes[index]
      if(element._id === id){
        notes[index].title = title;
        notes[index].description = description;
        notes[index].tag = tag;
      }
    }
   }
    return (
        <noteContext.Provider value={{ notes , addNotes , deleteNote , editNote , getallNotes}}>
            {props.children}
        </noteContext.Provider>
    )
}

export default NoteState
